            return
        }
        const dir=new google.maps.DirectionsService()
        dir.route({
            origin: {lat:place.latitude,lng:place.longitude},
            destination: {lat:tripArr[i+1].latitude,lng:tripArr[i+1].longitude},
            travelMode: 'DRIVING'
        })
        return dir
    })
