import React from "react";
import Endpoints from "./Form/Endpoints";

class AlgoSearchHome extends React.Component{
    state={Display:<Endpoints/>,startCity:"",startCountry:"",endCity:"",endCountry:"", stops:""}
    setSearch=(e,targ)=>{
        if(targ==='startCity'){
            this.setState({startCity:e.target.value})
        }
        if(targ==='startCountry'){
            this.setState({startCountry:e.target.value})
        }
        if(targ==='endCity'){
            this.setState({endCity:e.target.value})
        }
        if(targ==='endCountry'){
            this.setState({endCountry:e.target.value})
        }
        if(targ==='stops'){
            this.setState({stops:e.target.value})
        }
    }
    endPointSubmit=()=>{
        this.props.setValues({startCity:this.state.startCity,startCountry:this.state.startCountry,endCity:this.state.endCity,endCountry:this.state.endCountry,stopsReq:this.state.stopsReq})
        return
        //?
        //feels redundant with setvalues
        //calls function passed from AlgoHome, which makes API Req
    }
    render(){
        if(this.state.Display===Endpoints){
            return(
                <Endpoints locations={this.state} setSearch={this.setSearch} endPointSubmit={this.endPointSubmit}/>
            )
        }

    }
}

export default AlgoSearchHome
