import React,{useState} from "react";
import Sidebar from "./components/Sidebar";
import AttractionsHome from "./components/Attractions/AttractionsHome";
import axios from "axios";


const Main=(props)=>{

    const [selected,setSelected]=useState(0)
    const[visit,setVisit]=useState(false)
    const[message,setMessage]=useState('')
    console.log("trip",props.trip[selected])

    const save=async ()=>{
        const success=await axios.get('http://localhost:7000/tripSave')
    }

    const highlight=(index)=>{
        setSelected(index)
    }
    const go=(bool)=>{
        setVisit(bool)
    }
    if(visit){
        return(
            <div className="card" style={{margin:50}}>
                <AttractionsHome go={go} loc={props.trip[selected]} selected={selected} receiveTrip={props.receiveTrip}/>
            </div>
        )
    }
    return(
        <div className="card" style={{margin:50}}>
            <div className="columns">
                <div className="column is-three-fifths">

                </div>
                <div className="column is-two-fifths">
                    <Sidebar go={go} selected={selected} trip={props.trip} highlight={highlight}/>
                </div>
            </div>
        </div>
    )
}

export default Main
