import React,{useState} from "react";

const Attractions=(props)=>{
    const[sites,setSites]=useState({})
    const add=(place)=>{
        setSites(sites[place.name](place))
    }
    const remove=(place)=>{
        setSites(delete sites[place.name])
    }
    console.log('Sites',props.attractions)
    const filtered=props.sites.filter((place)=>{
        return place.photo&&place.name&&place.subtype[0]
    })
    const display=filtered.map((place)=>{
        const button=props['attractions'][place.name]?
            <button onClick={()=>{remove(place,'attractions')}}>Remove</button>:
            <button onClick={()=>{add(place)}}>Add</button>
        return(
            <div style={{width:400}}className="card">
                <div className="card-content">
                    <h1>{place.name}</h1>
                </div>
                <div className="content align">
                    <div className="card-image">
                        <img src={place.photo.images.thumbnail.url}></img>
                    </div>
                    <div className="align">
                        <div>
                            <p>Rating:{Math.floor(parseFloat(place.rating)*10)/10}</p>
                            <p>Address:{place.address_obj.street1+', '+place.address_obj.city}</p>
                        </div>
                        <div>
                            <p>Category:{place.subcategory[0].name}</p>
                        </div>
                        <div>
                            <a href={place.web_url} target="new"><i class="fa-solid fa-globe"></i></a>
                            {button}
                        </div>
                    </div>
                </div>
            </div>
        )
    })
    console.log('display',display)
    return(
        <div>
            <h1>Attractions</h1>
            {display}
        </div>
    )

}

export default Attractions
