import React from 'react';
import Directions from './Directions'
import { GoogleMap,MarkerF,DirectionsRenderer} from '@react-google-maps/api';

const Map=(props)=>{
    const markers=props.tripArr.map((place)=>{
        console.log(place)
        return <MarkerF position={{lat:place.latitude,lng:place.longitude}}/>
    })
    const dirs=props.tripArr.map(async (place,i)=>{
        if(i===props.tripArr.length-1){
            return
        }
        const dir= new google.maps.DirectionsService()
        const result=await dir.route({
            origin: {lat:place.latitude,lng:place.longitude},
            destination: {lat:props.tripArr[i+1].latitude,lng:props.tripArr[i+1].longitude},
            travelMode: google.maps.TravelMode.DRIVING
        })
        return dir
    })
    return(
        <GoogleMap zoom={10} center={{lat:props.loc.latitude,lng:props.loc.longitude}} mapContainerClassName="map-container" options={{fullScreenControl:false}}>
            {markers}
            <DirectionsRenderer directions={{dirs}}/>
        </GoogleMap>
    )
}

export default Map
