import React from "react";
import CityDisp from "./CityDisp";
import axios from "axios"

class ExtraCities extends React.Component{
    state={stopsSelected:[], cityText:"", countryText:""}

    updateText=(val,loc)=>{
        if(loc==='city'){
            this.setState({cityText:val})
        }
        if(loc==="country"){
            this.setState({countryText:val})
        }
    }

    //add variable for constant spacing relative to max number of stops

    addStop=async(e)=>{
        console.log('called')
        e.preventDefault()
        if(this.state.stopsSelected.length===parseInt(this.props.stops)){
            return
            //add error no more cities
        }
        const found=await this.confirm([this.state.cityText,this.state.countryText])
        //still have to be validated
        //wont work rn
        if(found){
            //issue needing to know city name
            //could change whole thing, although some other instances of iterating use key name, although that is changable
            this.setState({stopsSelected:[...this.state.stopsSelected,found.data],cityText:'',countryText:''})
        }
        //add error handling message

    }
    componentDidUpdate(){
        console.log(this.state.stopsSelected)
    }

    async confirm(valPairs){
        let info
        let qs=valPairs[0]+'_'+valPairs[1]
        try{
           info=await axios.get(`http://localhost:7000/${qs}/extra`)
        }catch(err){
            info=null
        }
        console.log(info)
        return info
    }
    //will add Search to validate

    render(){
        return(
        <div className="card conBox">
            <div style={{height:150}} className="center">
                <h1 style={{fontSize:25}}>Any specific places you want to stop?</h1>
                <br/>
                <h2 style={{fontSize:23}}>You can add up to <span style={{fontWeight:"bold"}}>{parseInt(this.props.stops)-this.state.stopsSelected.length}</span> stops.</h2>
            </div>
            <br/>
            <CityDisp cities={this.state.stopsSelected}/>
            <div className="center">
                <form action="/" onSubmit={(e)=>{e.preventDefault()}}>
                    <div className="field">
                        <label>City</label>
                        <input style={{width:250}} className="input is-danger" id="startpoint" type="text" value={this.state.cityText} onChange={(e)=>{this.updateText(e.target.value,'city')}} name="city" placeholder="City"/>
                        <input style={{width:250}} size="50" className="input is-danger" id="startCountry" type="text" value={this.state.countryText} onChange={(e)=>{this.updateText(e.target.value,'country')}} name="country" placeholder="Country"/>
                    </div>
                    <input className="button is-primary" type="submit" value="Add" onClick={(e)=>{this.addStop(e)}}/>
                    <input className="button is-danger" type="submit" value="Add" onClick={()=>{this.props.ExtraCities(this.state.stopsSelected)}}/>
                </form>
                <div></div>
                <h5 style={{color:"grey",fontSize:14,marginLeft:16,marginRight:16}}>(To be included, extra cities should be progressively far from the starting city, and should not involve considerable detours)</h5>
            </div>
        </div>
        )
    }
}








export default ExtraCities

