import React,{useState,useEffect} from "react";
import Homepage from "./components/homepage/Homepage";
import AlgoHome from "./components/Algo/AlgoHome";
import Navbar from "./components/navbar/Navbar";
import Main from "./components/display/Main";
import Background from './images/background_map.jpeg'
import axios from "axios";
import './css/style.css'


function App() {
  const [trip,setTrip]=useState(null)
  const [display,setDisplay]=useState('home')
  const [signIn,setSignIn]=useState(null)

  useEffect(()=>{
    if(trip){
      console.log('trip received')
      setDisplay("tripDisplay")
    }else{
      return
    }
  },[trip])
  useEffect(()=>{
    (async()=>{
      let status=await axios.get('http//localhost:7000/validate')
      setSignIn(status.data)
    })()
  },[])

  const setAlgo=()=>{
    setDisplay('algo')
  }

  const receiveTrip=(tripPlan)=>{
    setTrip(tripPlan)
  }

  const signedIn=(val)=>{
    setSignIn(val)
  }

  let current

  if(display==='home'){
    current=<Homepage setAlgo={setAlgo}/>
  }else if(display==='algo'){
    current=<AlgoHome receiveTrip={receiveTrip}/>
  }else if(display==='tripDisplay'){
    current=<Main trip={trip} receiveTrip={receiveTrip}/>
  }
  return(
    <div style={{backgroundImage:`url(${Background})`,backgroundSize:1500,backgroundPositionX:0,backgroundPositionY:-220}}>
      <Navbar signedIn={signedIn} signIn={signIn}/>
      {current}
    </div>
  )
}

export default App;
