import React,{useState,useEffect} from 'react';
import {GoogleMap,MarkerF,DirectionsRenderer} from '@react-google-maps/api';


const Map=(props)=>{
    const[markers,setMarkers]=useState(null)
    const [secondaryMarkers,setSecondary]=useState(null)
    const[directions,setDirections]=useState(null)

    useEffect(()=>{
        console.log('called')
        const markersArr=props.tripArr.map((place)=>{
            console.log(place)
            return <MarkerF position={{lat:place.latitude,lng:place.longitude}} key={place.name}/>
        })
        setMarkers(markersArr)
    },[props.tripArr])

    useEffect(()=>{
        console.log('running sec',props.tripArr[props.selected])
        let secMarkersCollection=[]
        if(props.selected===null){
            return
        }
        if(props.tripArr[props.selected].hotels){
            console.log('hotel')
            const hMarks=[]
            const list=props.tripArr[props.selected].hotels
            for(let hotel in list){
                hMarks.push(<MarkerF position={{lat:list[hotel].latitude,lng:list[hotel].longitude}} label={list[hotel].hotel_name}key={list[hotel].hotel_name}style={{color:'pink'}}/>)
            }
            secMarkersCollection=secMarkersCollection.concat(hMarks)
        }
        if(props.tripArr[props.selected].restaurants){
            const rMarks=[]
            const list=props.tripArr[props.selected].restaurants
            for(let rest in list){
                rMarks.push(<MarkerF position={{lat:list[rest].latitude,lng:list[rest].longitude}} label={list[rest].name}key={list[rest].name}style={{color:'green'}}/>)
            }
            secMarkersCollection=secMarkersCollection.concat(rMarks)
        }
        if(props.tripArr[props.selected].attractions){
            const aMarks=[]
            const list=props.tripArr[props.selected].attractions
            for (let attr in list){
                aMarks.push(<MarkerF position={{lat:parseFloat(list[attr].latitude),lng:parseFloat(list[attr].longitude)}} label={list[attr].name}key={list[attr].name}style={{color:'blue'}}/>)
            }
            secMarkersCollection=secMarkersCollection.concat(aMarks)
        }
        console.log(secMarkersCollection)
        if(secMarkersCollection.length){
            setSecondary(secMarkersCollection)
        }
        return
    },[props.selected])

    useEffect(()=>{
        console.log('called')
        if(!props.selected){
            return
        }
        let use
        if(props.selected===props.tripArr.length-1){
            use=props.selected-1
        }else{
            use=props.selected
        }
        //add abort controller
        (async()=>{
            const dir= new window.google.maps.DirectionsService()
            const result=await dir.route({
                origin: {lat:props.tripArr[use].latitude,lng:props.tripArr[use].longitude},
                destination: {lat:props.tripArr[use+1].latitude,lng:props.tripArr[use+1].longitude},
                travelMode: window.google.maps.TravelMode.DRIVING
            })
            console.log('result',result)
            setDirections(result)
        })()
    },[secondaryMarkers,props.selected,props.tripArr])

    const loadedMarkers=markers?markers:''

    const loadedDirections=directions?<DirectionsRenderer directions={directions.routes[0]}/>:''
    let current=props.selected||props.selected===0?
        {lat:props.tripArr[props.selected].latitude,lng:props.tripArr[props.selected].longitude,zoom:9}:
        {lat:props.tripArr[Math.floor(props.tripArr.length/2)].latitude,lng:props.tripArr[Math.floor(props.tripArr.length/2)].longitude,zoom:props.zoom}
    return(
        <GoogleMap zoom={current.zoom} center={{lat:current.lat,lng:current.lng}} mapContainerClassName="map-container" options={{fullScreenControl:false}}>
            {loadedMarkers}
            {loadedDirections}
            {secondaryMarkers}
        </GoogleMap>
    )
}

export default Map
