import React from "react";

const Sidebar=(props)=>{
    const places=props.trip.map((stop,i)=>{
        const styling=props.selected===i?{borderColor:"red"}:{}
        return(
            <div style={styling}onClick={()=>props.highlight(i)} className="card">
                <div className="card-content">
                    <h1>{i+1}. {stop.name}</h1>
                </div>
                <div className="content">
                    <p>Country: {stop.code}</p>
                    <br/>
                    <p>Population: {stop.population}</p>
                    {i===props.trip.length?{}:
                    <p>Next Stop: {Math.floor(stop.distanceNext)}km away</p>}
                </div>
            </div>
        )
    })
    return(
        <div>
            {places}
        </div>
    )
}
export default Sidebar
