import React,{useState,useEffect} from "react";
import IndexIcons from "./IndexIcons";


const Outline=(props)=>{
    const useEffect=(()=>{
        console.log('rendered')
    })
    const [index,setIndex]=useState(0)
    const opts=[["Equidistant Cities",'eq'],["Large Cities","lg"]]
    const outlinedArr=props.outline.map=((stop,i)=>{
        return(
        <div className="column is-one-third" style={{marginLeft:10,marginRight:10}}>
            <div class="card cardS">
                <div class="card-content center">
                    <div class="content">
                        <h3>{""+(i+1)+"."}</h3>
                        <h2>{stop['name']+","}</h2><h3>{stop['country']}</h3>
                        <h4> Population:{stop['population']}</h4>
                        <h4> Distance to Next Stop:{stop['distanceNext']}</h4>
                    </div>
                </div>
            </div>
        </div>
        )
    })
    const scroll=(num)=>{
        if(num===1&&index>=outlinedArr.length-2||num===0&&index===0){
            return
        }
        setIndex(index+num)
    }
    return(
        <div className="card cardS conBox center">
            <div className="horizontal">
                <button onClick={()=>{scroll(-1)}}><i className="fa-solid fa-arrow-right"></i></button>
                <div className="columns confirmation">
                    {outlinedArr[index]}
                    {outlinedArr[index+1]}
                </div>
                <i onClick={()=>{scroll(1)}}></i>
            </div>
            <IndexIcons index={index} outline={outlinedArr}/>
            <div>
                <form onSubmit={(e)=>{e.preventDefault()}}>
                    <input className="button is-danger" type="submit" >Generate my trip</input>
                </form>
            </div>
        </div>
    )
}

export default Outline
