import React from "react";

const Sidebar=(props)=>{
    const places=props.trip.map((stop,i)=>{
        const styling=props.selected===i?{borderColor:'#00d1b2'}:{}
        return(
            <div style={styling}onClick={()=>props.highlight(i)} className="card">
                <div className="card-content">
                    <h1>{i+1}. {stop.name}</h1>
                </div>
                <div className="content">
                    <p>,{stop.code}</p>
                    <br/>
                    <h4>Population:{stop.population}</h4>
                    <h4>Next Stop:{Math.floor(stop.distNext)}km</h4>
                </div>
            </div>
        )
    })
    return(
        <div>
            {places}
        </div>
    )
}
export default Sidebar
