import React,{useState} from "react";
import OutlineCities from "./OutlineCities";


const Outline=(props)=>{
    const [mode,setMode]=useState('eq')
    const [index,setIndex]=useState(0)
    const opts=[["Equidistant Cities",'eq'],["Large Cities","lg"]]
    const outlinedArr=OutlineCities(props.outline)
    return(
        <div className="card cardS conBox center">
            <i></i>
            <div className="columns confirmation">
                {outlinedArr[index]}
                {outlinedArr[index+1]}
            </div>
            <i></i>
            <div class="control">
                <label class="radio">
                    <input type="radio" name="answer" onClick={setMode('eq')}checked={mode==='eq'}></input>
                    Equidistant Cities
                </label>
                <label class="radio">
                    <input type="radio" name="answer"  onClick={setMode('lg')} checked={mode==='lg'}></input>
                    Large Cities
                </label>
            </div>
        </div>
    )
}

export default Outline
