import React from 'react';
import Directions from './Directions'
import { GoogleMap,MarkerF} from '@react-google-maps/api';

const Map=(props)=>{
    const markers=props.tripArr.map((place)=>{
        console.log(place)
        return <MarkerF position={{lat:place.latitude,lng:place.longitude}}/>
    })
    return(
        <GoogleMap zoom={10} center={{lat:props.loc.latitude,lng:props.loc.longitude}} mapContainerClassName="map-container">
            {markers}
            <Directions tripArr={props.tripArr}/>
        </GoogleMap>
    )
}

export default Map
