import React from "react";
import Endpoints from "./Form/Endpoints";
import Confirmation from "./Form/confirmation/Confirmation";

class AlgoSearchHome extends React.Component{
    state={display:'endpoints',startCity:"",startCountry:"",endCity:"",endCountry:"", stops:""}
    setSearch=(value,targ)=>{
        if(targ==='startCity'){
            this.setState({startCity:value})
        }
        if(targ==='startCountry'){
            this.setState({startCountry:value})
        }
        if(targ==='endCity'){
            this.setState({endCity:value})
        }
        if(targ==='endCountry'){
            this.setState({endCountry:value})
        }
        if(targ==='stops'){
            this.setState({stops:value})
        }
    }
    endPointSubmit=(e)=>{
        e.preventDefault()
        this.props.setValues(this.state.startCity+"_"+this.state.startCountry+"_"+this.state.endCity+"_"+this.state.endCountry+"_"+this.state.stops)
        return
        //?
        //feels redundant with setvalues
        //calls function passed from AlgoHome, which makes API Req
    }

    //Dont want this actually useEffect would be better
    componentDidUpdate(){
        console.log('did update',this.state.display,this.props.data)
        if(this.props.data&&this.state.display==='endpoints'){
            console.log('hpwdy')
            this.setState({display:'confirmation'})
        }
    }
    //some of these might be redundant
    pointsConfirmed(){
        this.setState('extra')
    }

    returnEndpoints=()=>{
    }

    render(){
        if(this.state.display==='endpoints'){
            return(
                <Endpoints locations={this.state} setSearch={this.setSearch} clearState={this.clearState} endPointSubmit={this.endPointSubmit}/>
            )
        }
        if(this.state.display==='confirmation'){
            return(
                <Confirmation data={this.props.data} pointsConfirmed={this.pointsConfirmed} returnEndpoints={this.returnEndpoints}/>
            )
        }


    }
}

export default AlgoSearchHome
