import React from "react";

const Sidebar=(props)=>{
    const places=props.trip.map((stop,i)=>{
        const styling=props.selected===i?{color:"red",marginTop:5,width:450}:{marginTop:5}
        const next=i===props.trip.length-1?"":`Next Stop: ${Math.floor(stop.distanceNext)} km away`
        return(
            <div style={styling}onClick={()=>props.highlight(i)} className="card">
                <div style={{fontSize:20,fontWeight:7}}className="card-content">
                    <h1 style={{marginTop:-15}}>{i+1}. {stop.name}</h1>
                </div>
                <div style={{marginLeft:35,marginBottom:5,marginTop:-5}}className="content">
                    <div style={{marginBottom:5}}>
                        <p>Country: {stop.code}</p>
                        <p>Population: {stop.population}</p>
                        <p>{next}</p>
                        {!next? <br></br>:""}
                    </div>
                </div>
            </div>
        )
    })
    return(
        <div style={{width:500,backgroundColor:'lightGrey'}}>
            {places}
        </div>
    )
}
export default Sidebar
