import React,{useState,useEffect} from 'react';
import {GoogleMap,MarkerF,DirectionsRenderer} from '@react-google-maps/api';


const Map=async(props)=>{
    const[markers,setMarkers]=useState([])

    useEffect(()=>{
        setMarkers(props.tripArr.map((place)=>{
            console.log(place)
            return <MarkerF position={{lat:place.latitude,lng:place.longitude}}/>
        }))
    },[props.tripArr])

    return(
        <GoogleMap zoom={10} center={{lat:props.tripArr[props.selected].latitude,lng:props.tripArr[props.selected].longitude}} mapContainerClassName="map-container" options={{fullScreenControl:false}}>
            {markers}
        </GoogleMap>
    )
}

export default Map
