import React,{useState,useEffect} from "react";
import Sidebar from "./components/Sidebar";
import AttractionsHome from "./components/Attractions/AttractionsHome";
import Map from "./components/Map";
import axios from "axios";
import Restaurants from "./components/Attractions/Specific/Restaurants";
import { useLoadScript } from "@react-google-maps/api";


const Main=(props)=>{

    const [selected,setSelected]=useState(0)
    const[visit,setVisit]=useState(false)
    const[message,setMessage]=useState('')
    const[activeTrip,setActiveTrip]=useState(null)
    const[tripArr,setTripArr]=useState([])

    const{isLoaded}=useLoadScript({googleMapsApiKey:'AIzaSyCbHVClKTILxFe3bJTShVUWJimPKJRqhyE'})

    useEffect(()=>{
        console.log('running',props.trip)
        if(Array.isArray(props.trip)){
            setTripArr(props.trip)
        }else{
            setTripArr(props.trip.trip)
            setActiveTrip(props.trip._id)
        }
    },[props.trip])

    useEffect(()=>{
        if(!activeTrip){
            return
        }
        (async()=>{
            const synced=await(axios.get(`http://localhost:7000/select/${activeTrip}`))
            if(synced){
                console.log('backend synced')
                return
            }else{
                console.log('unable to sync')
                //set up view mode
                return
            }
        })()
    },[activeTrip])

    useEffect(()=>{
        setMessage('')
    },[props.signIn])

    console.log("trip",tripArr)

    const save=async ()=>{
        if(!props.signIn){
            setMessage('To save your trip, please sign in or create an account')
            return
        }
        let qsTrip=activeTrip?activeTrip:'0'
        const success=await axios.get(`http://localhost:7000/tripSave/${props.signIn}/${qsTrip}`)
        if(success.data){
            console.log('successfully saved')
            setActiveTrip(success.data)
            return 
        }else{
            console.log('saving error')
            return
        }
    }

    const highlight=(index)=>{
        setSelected(index)
    }
    const go=(bool)=>{
        setVisit(bool)
    }

    const messageDisp=message?
    <h3 style={{color:'red'}}>{message}</h3>:''


    if(visit){
        return(
            <div className="card" style={{margin:50}}>
                <AttractionsHome go={go} loc={tripArr[selected]} selected={selected} receiveTrip={props.receiveTrip}/>
            </div>
        )
    }
    return(
        <div className="card" style={{margin:50}}>
            <div className="columns">
                <div className="column is-three-fifths">
                    {!isLoaded?
                    <div>Loading...</div>:
                    <Map selected={selected} trip={tripArr}/>
                    }
                </div>
                <div className="column is-two-fifths">
                    <Sidebar go={go} loc={tripArr[selected]} trip={tripArr} highlight={highlight}/>
                </div>
            </div>
            <button onClick={(save)}>Save</button>
            {messageDisp}
        </div>
    )
}

export default Main
