import React from "react";

class SignUp extends React.Component{
    state={username:'',password:'',passwordConfirmation:''}

    create(){
        if(this.state.password!==this.state.passwordConfirmation){
            return
        }
    }

    update(val,target){
        if(target==='u'){
            this.setState({username:val})
        }
        if(target==='p'){
            this.setState({password:val})
        }
        if(target==='pc'){
            this.setState({passwordConfirmation:val})
        }
    }

    render(){
        const message=this.state.password===this.state.passwordConfirmation?
            <h3 style={{fontColor:'red'}}>Password</h3>:''
        return(
            <form style={{marginTop:10}} action="/">
                <div>
                    <label style={{marginLeft:-220}}>Username</label>
                    <div className="field">
                        <input style={{width:300}} className="input is-danger" id="startpoint" type="text" value={this.state.username} onChange={(e)=>{this.update(e.target.value,'u')}} name="username" placeholder="Username"/>
                    </div>
                </div>
                <br/>
                <div>
                    <label style={{marginLeft:-220}}>Password</label>
                    <div className="field">
                        <input style={{width:300}} className="input is-danger" id="endpoint" type="text" value={this.update.password} onChange={(e)=>{this.update(e.target.value,'p')}} name="password" placeholder="Password"/>
                    </div>
                </div>
                <br/>
                <div>
                    <label style={{marginLeft:-220}}>Password Confirmation</label>
                    <div className="field">
                        <input style={{width:300}} className="input is-danger" id="endpoint" type="text" value={this.state.passwordConfirmation} onChange={(e)=>{this.update(e.target.value,'pc')}} name="passwordConfirmation" placeholder="Password Confirmation"/>
                    </div>
                </div>
                <br/>
                <input className="button is-primary" type="submit" value="Create Account"/>

            </form>
        )
    }
}

export default SignUp
