import React from "react";

const Endpoints=(props)=>{

    const missing=()=>{
        if(props.missing){
            return <h3 style={{color:'red'}}>Please enter a non-zero number</h3>
        }
    }
    const sState=props.locations.sState!==null?<input style={{width:50}} className="input is-danger" id="startState" type="text" value={props.locations.sState} onChange={(e)=>{props.setSearch(e.target.value,'sState')}} name="startState" placeholder="Starting State (Optional)"/>:""
    const eState=props.locations.eState!==null?<input style={{width:50}} className="input is-danger" id="startState" type="text" value={props.locations.eState} onChange={(e)=>{props.setSearch(e.target.value,'eState')}} name="endState" placeholder="End State (Optional)"/>:""
    const msg=(val)=>{
        console.log(props.bad)
        return props.bad&&props.bad===val?<h3 style={{color:'red'}}>Sorry, city not found</h3>:""
    }
    return(
        <React.Fragment>
            <progress class="progress is-success is-normal" value="0" max="100">0%</progress>
            <br/>
            <div className="card cardS" >
                <div className="card-content card-contentS">
                    <form action="/" onSubmit={props.endPointSubmit}>
                        <label>Starting City   </label>
                        <div className="field">
                            <input style={{width:250}} className="input is-danger" id="startpoint" type="text" value={props.locations.startCity} onChange={(e)=>{props.setSearch(e.target.value,'startCity')}} name="startpoint" placeholder="Starting City"/>
                            <span style={{padding:5}}></span>
                            <input style={{width:250}} size="50" className="input is-danger" id="startCountry" type="text" value={props.locations.startCountry} onChange={(e)=>{props.setSearch(e.target.value,'startCountry')}} name="startCountry" placeholder="Starting Country"/>
                            {sState}
                            {msg('0')}
                        </div>
                        <br/>
                        <label>Ending City   </label>
                        <div className="field">
                            <input style={{width:250}} className="input is-danger" id="endpoint" type="text" value={props.locations.endCity} onChange={(e)=>{props.setSearch(e.target.value,'endCity')}} name="endpoint" placeholder="Ending City"/>
                            <span style={{padding:5}}></span>
                            <input style={{width:250}} className="input is-danger" id="endCountry" type="text"value={props.locations.endCountry} onChange={(e)=>{props.setSearch(e.target.value,'endCountry')}} name="endCountry" placeholder="Ending Country"/>
                            {eState}
                            {msg('1')}
                        </div>
                        <br/>
                        <div className="field">
                            <label for="stops">Stops</label>
                            <input style={{width:125}} className="input is-danger"type="text" name="stops" value={props.locations.stops} onChange={(e)=>{props.setSearch(e.target.value,'stops')}}/>
                            {missing()}
                        </div>
                        <br/>
                        <input className="button is-primary" type="submit" value="Let's Go!" onClick={(e)=>{props.endPointSubmit(e)}}/>
                    </form>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Endpoints
