import React from "react";

const Restaurants=(props)=>{
    console.log('called')
    if(!props.restaurants.length){
        return
    }
    const filtered=props.restaurants.filter((place)=>{
        return place.photo&&place.name&&place.cuisine[0]&&place.location_id
    })
    console.log(filtered)
    const display=filtered.map((place)=>{
        const button=props.attractions['restaurants'][place.name]?
            <button onClick={()=>{props.remove(place,'restaurants')}}>Remove</button>:
            <button onClick={()=>{props.add(place,'restaurants')}}>Add</button>
        return(
            <div style={{width:400,height:200,margin:5}}className="card">
                <div style={{marginTop:2}} className="card-content">
                    <h1>{place.name}</h1>
                </div>
                <div style={{marginLeft:20,marginBottom:15,position:'absolute'}} className="content align">
                    <div className="card-image">
                        <img style={{height:75,width:75}} src={place.photo.images.thumbnail.url}></img>
                    </div>
                    <div className="align">
                        <div style={{marginLeft:10,marginTop:7}}>
                            <p><h3>{Math.floor(parseFloat(place.rating)*10)/10}</h3>/5</p>
                        </div>
                        <div style={{marginLeft:25,marginTop:8}}>
                            <p>Price:{place.price_level}</p>
                            <p>Cuisine:{place.cuisine[0].name}</p>
                            {place.district?<p>Neighborhood:{place.neighborhood_info.name}</p>:<p></p>}
                        </div>
                        <div>
                            <a href={place.web_url} target="new"><i class="fa-solid fa-globe"></i></a>
                            {button}
                        </div>
                    </div>
                </div>
            </div>
        )
    })
    console.log('display',display)
    return(
        <React.Fragment>
            <h1>Restaurants</h1>
            {display}
        </React.Fragment>
    )
}

export default Restaurants
