import React,{useState} from "react";
import Sidebar from "./components/Sidebar";
import AttractionsHome from "./components/Attractions/AttractionsHome";

const Main=(props)=>{
    const [selected,setSelected]=useState(null)
    const[visit,setVisit]=useState(false)
    const highlight=(index)=>{
        setSelected(index)
    }
    const go=(bool)=>{
        setVisit(bool)
    }
    if(visit){
        return(
            <div className="card" style={{margin:50}}>
                <AttractionsHome loc={props.trip[selected]}/>
            </div>
        )
    }
    return(
        <div className="card" style={{margin:50}}>
            <div className="columns">
                <div className="column is-three-fifths">

                </div>
                <div className="column is-two-fifths">
                    <Sidebar go={go} selected={selected} trip={props.trip} highlight={highlight}/>
                </div>
            </div>
        </div>
    )
}

export default Main
