import axios from "axios";
import React,{useState} from "react";
import Retrieve from "./Retrieve";

const AttractionsHome=(props)=>{
    console.log(props.loc)

    const[attractions,setAttractions]=useState({hotels:{},restaurants:{},attractions:{}})

    const save=async()=>{
        let qs=""
        for(let key in attractions){
            if(key==='hotels'){
                qs+='hotels_'
            }else if(key==='restaurants'){
                qs+="restaurants_"
            }else if(key==='attractions'){
                qs+='attractions_'
            }
            for(let place in key){
                if(key==='hotels'){
                    qs=qs+place.ufi+"_"
                }else{
                    qs=qs+place.location_id+"_"
                }
            }
        }
        if(await axios.get(`https://localhost:7000/sync/${props.selected}/${qs}`)){
            console.log('backend saved')
        }
    }

    const add=(place,category)=>{
        let copy=Object.assign({},attractions)
        copy[category]=place.name
        setAttractions(copy)
    }
    const remove=(place,category)=>{
        let copy=Object.assign({},attractions)
        delete copy[category][place.name]
        setAttractions(copy)
    }
    return(
        <div className="card">
            <button onClick={()=>{props.go(false)}}> Go back</button>
            <div className="columns">
                <div className="column-one-third">
                    <Retrieve type={'hotels'}loc={props.loc} add={add} remove={remove} attractions={attractions}/>
                </div>
                <div className="column-one-third">
                    <Retrieve type={'restaurants'} loc={props.loc} add={add} remove={remove} attractions={attractions}/>
                </div>
                <div className="column-one-third">
                    <Retrieve type={'attractions'} loc={props.loc} add={add} remove={remove} attractions={attractions}/>
                </div>
            </div>
        </div>
    )
}
//Note: Attraction is passed as type because it is needed for api, switches to more succinct attr after

export default AttractionsHome
