import React,{useState} from "react";

const Hotels=(props)=>{
    console.log('hotels',props.hotels)
    const[hotels,setHotels]=useState([])
    //could maybe be shared between categories
    const add=(place)=>{
        setHotels(hotels.push(place))
    }
    const remove=(place)=>{
        const updated=hotels.filter((loc)=>{
            loc.name!==place.name
        })
        setHotels(updated)
    }
    const display=props.hotels.map((place)=>{
        return(
            <div style={{width:400}}className="card">
                <div className="card-content">
                    <h1>{place.hotel_name}</h1>
                </div>
                <div className="content align">
                    <div className="card-image">
                        <img src={place.main_photo_url}></img>
                    </div>
                    <div className="align">
                        <div>
                            <p>City:{place.city_trans}</p>
                            <p>Class:{place.class} Stars</p>
                        </div>
                        <div>
                            {place.district?<p>Neighborhood:{place.district}</p>:<p></p>}
                            <a href={place.url} target="new"><i class="fa-solid fa-globe"></i></a>
                        </div>
                    </div>
                </div>
            </div>
        )
    })
    console.log('display',display)
    return(
        <div>
            <h1>Hotels</h1>
            {display}
        </div>
    )
}
export default Hotels
