import React from "react"

const CityDisp=(props)=>{
    //need to update if sending obj
    let counter=0
    const cityD=props.cities.map((city,i)=>{
        const up=<i onClick={()=>{props.shuffle('up')}} class="fa-solid fa-up"></i>
        const down=<i onClick={()=>{props.shuffle('down')}}class="fa-solid fa-down"></i>
        let icons
        if(i===0){
            icons=down
        }else if(i===props.cities.length){
            icons=up
        }else{
            icons=up+down
        }
        counter++
        return(
            //would prefer slightly lighter grey
            <li style={{color:'light grey',marginLeft:25}}>
                {counter}. {city.name+', '+city.country}
            <span style={{alignItems:'right'}}>{icons}</span>
            </li>
        )
    })
    return(
        <div className="extraCityBox box">
            <div style={{height:100,maxHeight:150,marginTop:10,}}>
                <h3 style={{color:'light grey',marginLeft:3}}>Cities:</h3>
                <ul>
                    {cityD}
                </ul>
            </div>
        </div>
    )
}

export default CityDisp
