import React from "react";
import axios from "axios"
import AlgoSearchHome from "./Search/AlgoSearchHome";

class AlgoHome extends React.Component{
    state={start:null,end:null,mid:null,data:null}
    setValues=async(valPairs)=>{
        console.log(valPairs)
        const info=await axios.get(`http://localhost:7000/${valPairs}/confirm`)
        console.log(info)
        this.setState({data:info[0],distance:info[1]})
    }
    clearState=()=>{
        this.setState({start:null,end:null,mid:null,data:null,distance:null})
    }
    render(){
        return(
            <div>
                <AlgoSearchHome data={this.state.data} distance={this.state.distance}setValues={this.setValues} clearState={this.clearState}/>
            </div>
        )
    }
}
export default AlgoHome
