import React from "react";

class SignUp{
    state={username:'',password:'',passwordConfirmation:''}

    create(){
        if(this.state.password!==passwordConfirmation){
            return
        }
    }

    update(val,target){
        if(target==='u'){
            this.setState({username:val})
        }
        if(target==='p'){
            this.setState({password:val})
        }
        if(target==='pc'){
            this.setState({passwordConfirmation:val})
        }
    }

    render(){
        const message=this.state.password===this.state.passwordConfirmation?
            <h3 style={{fontColor:'red'}}>Password</h3>:''
        return(
            <div className="card cardS" >
                <div className="card-content card-contentS">
                    <form action="/" onSubmit={props.endPointSubmit}>
                        <label>Username</label>
                        <div className="field">
                            <input style={{width:250}} className="input is-danger" id="startpoint" type="text" value={this.state.username} onChange={(e)=>{this.update(e.target.value,'u')}} name="username" placeholder="Username"/>
                        </div>
                        <br/>
                        <label>Password</label>
                        <div className="field">
                            <input style={{width:250}} className="input is-danger" id="endpoint" type="text" value={this.update.password} onChange={(e)=>{this.update(e.target.value,'p')}} name="password" placeholder="Password"/>
                        </div>
                        <br/>
                        <label>Password Confirmation</label>
                        <div className="field">
                            <input style={{width:250}} className="input is-danger" id="endpoint" type="text" value={this.state.passwordConfirmation} onChange={(e)=>{this.update(e.target.value,'pc')}} name="passwordConfirmation" placeholder="Password Confirmation"/>
                        </div>
                        <br/>
                        <input className="button is-primary" type="submit" value="Create Account" onClick={(e)=>{props.endPointSubmit(e)}}/>
                        {message}
                    </form>
                </div>
            </div>
        )
    }
}

export default SignUp
