import React,{useState,useEffect} from 'react';
import {GoogleMap,MarkerF,DirectionsRenderer} from '@react-google-maps/api';


const Map=async(props)=>{
    const[directions,setDirections]=useState(null)
    const[markers,setMarkers]=useState([])

    useEffect(()=>{
        setMarkers(props.tripArr.map((place)=>{
            console.log(place)
            return <MarkerF position={{lat:place.latitude,lng:place.longitude}}/>
        }))
    },[props.tripArr])

    useEffect(()=>{
        if(!props.loc){
            return
        }
        let use
        if(props.selected===props.tripArr.length-1){
            use=props.selected-1
        }else{
            use=props.selected
        }
        //add abort controller
        (async()=>{
            const dir= new window.google.maps.DirectionsService()
            const result=await dir.route({
                origin: {lat:props.tripArr[use].latitude,lng:props.tripArr[use].longitude},
                destination: {lat:props.tripArr[use+1].latitude,lng:props.tripArr[use+1].longitude},
                travelMode: window.google.maps.TravelMode.DRIVING
            })
            setDirections(result)
        })()
    },[props.selected,props.tripArr])



    const loadedDirections=directions?<DirectionsRenderer directions={{directions}}/>:''
    return(
        <GoogleMap zoom={10} center={{lat:props.loc.latitude,lng:props.loc.longitude}} mapContainerClassName="map-container" options={{fullScreenControl:false}}>
            {markers}
            {loadedDirections}
        </GoogleMap>
    )
}

export default Map
