import React,{useState} from "react";

const SignIn=()=>{

    //they could actually share state i guess
    const [username,setUsername]=useState('')
    const [password,setPassword]=useState('')

    const update=(val,target)=>{
        if(target==='u'){
            setUsername(val)
        }
        if(target==='p'){
            setPassword(val)
        }
    }


    return(
        <form style={{marginTop:10}} action="/" >
            <div>
                <label style={{marginLeft:-220}}>Username</label>
                <div className="field">
                    <input style={{width:300}} className="input is-danger" id="startpoint" type="text" value={username} onChange={(e)=>{this.update(e.target.value,'u')}} name="username" placeholder="Username"/>
                </div>
            </div>
            <br/>
            <div>
                <label style={{marginLeft:-220}}>Password</label>
                <div className="field">
                    <input style={{width:300}} className="input is-danger" id="endpoint" type="text" value={password} onChange={(e)=>{this.update(e.target.value,'p')}} name="password" placeholder="Password"/>
                </div>
            </div>
            <br/>
            <input className="button is-primary" type="submit" value="Sign In" />
        </form>
    )
}

export default SignIn
