import React from "react";
import axios from "axios"
import AlgoSearchHome from "./Search/AlgoSearchHome";

class AlgoHome extends React.Component{
    state={start:null,end:null,mid:null,data:null,bad:null}
    setValues=async(valPairs)=>{
        console.log(valPairs)
        const req=await axios.get(`http://localhost:7000/${valPairs}/confirm`)
        if(req[0]===false){
            this.setState({bad:req[1]})
        }
        const data=req.data
        console.log(data)
        this.setState({data:data[0],distance:data[1],bad:null})
    }
    extraStops=(arr)=>{
        //checks distance from starting city to each added city
        //orders based on distance
        //checks validity assuming rest of stops are valid
        //if stop is invalid, removes, recalculates validity based on new number of stops
        //once list is calculated, compares total stops to stops used, assigns midpoints accordingly
    }

    clearState=()=>{
        this.setState({start:null,end:null,mid:null,data:null,distance:null,bad:null})
    }
    render(){
        return(
            <div>
                <AlgoSearchHome data={this.state.data} distance={this.state.distance} setValues={this.setValues} clearState={this.clearState} extraStops={this.extraStops}/>
            </div>
        )
    }
}
export default AlgoHome
