import React from 'react';
import { GoogleMap,Marker} from '@react-google-maps/api';

const Map=()=>{
    const markers=props.tripArr.map((place)=>{
        return <Marker position={{lat:place.lattitude,lng:place.longitude}}/>
    })
    return(
        <GoogleMap zoom={10} center={{lat:props.loc.lattitude,lng:props.loc.longitude}} mapContainerClassName="map-container">
            {markers}
        </GoogleMap>
    )
}

export default Map
