import React,{useState} from "react";

const Buttons=()=>{
    const buttons=["Let's go!","How does it work?"]
    const [hoverButton, setButton]=useState(null)
    const buttonMap=buttons.map((button,index)=>{
        let status=hoverButton===index?'is-active':""
        return(
            <button className={status}>
                {button}
            </button>
        )
    })
    return(
        <div>
            {buttonMap}
        </div>
    )

}
export default Buttons
