import React,{useState} from "react";
import OutlineCities from "./OutlineCities";
import IndexIcons from "./IndexIcons";


const Outline=(props)=>{
    const [index,setIndex]=useState(0)
    const opts=[["Equidistant Cities",'eq'],["Large Cities","lg"]]
    const outlinedArr=OutlineCities(props.outline)
    const scroll=(num)=>{
        if(num===1&&index>=outlinedArr.length-2||num===0&&index===0){
            return
        }
        setIndex(index+num)
    }
    return(
        <div className="card cardS conBox center">
            <div className="horizontal">
                <button onClick={()=>{scroll(-1)}}><i className="fa-solid fa-arrow-right"></i></button>
                <div className="columns confirmation">
                    {outlinedArr[index]}
                    {outlinedArr[index+1]}
                </div>
                <i onClick={()=>{scroll(1)}}></i>
            </div>
            <IndexIcons index={index} outline={outlinedArr}/>
            <div>
                <form onSubmit={(e)=>{preventDefault(e)}}>
                    <div class="control">
                        <label class="radio">
                            <input type="radio" name="answer" onClick={props.setMode('eq')}checked={props.mode==='eq'}></input>
                            Equidistant Cities
                        </label>
                        <label class="radio">
                            <input type="radio" name="answer"  onClick={props.setMode('lg')} checked={props.mode==='lg'}></input>
                            Large Cities
                        </label>
                    </div>
                    <input className="button is-danger" type="submit" ></input>
                </form>
            </div>
        </div>
    )
}

export default Outline
